contracts/StrategyManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/IYieldOptimizer.sol";

/**
 * @title StrategyManager
 * @dev Manages individual yield farming strategies and protocol interactions
 */
contract StrategyManager is Initializable, OwnableUpgradeable, ReentrancyGuardUpgradeable {
    using SafeERC20 for IERC20;

    // Structs
    struct ProtocolAdapter {
        address adapterAddress;
        string protocolName;
        bool isActive;
        uint256 totalAllocated;
        uint256 averageAPY;
    }

    struct ExecutionParams {
        address protocol;
        address token;
        uint256 amount;
        bytes data;
        uint256 minReturn;
        uint256 deadline;
    }

    // State variables
    mapping(address => ProtocolAdapter) public protocolAdapters;
    mapping(bytes32 => bool) public executedStrategies;
    
    address public yieldOptimizer;
    address[] public supportedProtocols;
    
    uint256 public constant SLIPPAGE_TOLERANCE = 100; // 1%
    uint256 public executionFee; // in basis points

    // Events
    event StrategyExecuted(
        bytes32 indexed strategyId,
        address protocol,
        uint256 amount,
        uint256 actualReturn
    );
    event ProtocolAdapterAdded(address protocol, address adapter);
    event StrategyReverted(bytes32 indexed strategyId, string reason);
    event EmergencyWithdraw(address protocol, address token, uint256 amount);

    // Modifiers
    modifier onlyYieldOptimizer() {
        require(msg.sender == yieldOptimizer, "Only YieldOptimizer can call");
        _;
    }

    modifier validProtocol(address protocol) {
        require(protocolAdapters[protocol].isActive, "Protocol not supported");
        _;
    }

    // Remove the constructor
    // constructor(address _yieldOptimizer) Ownable(msg.sender) {
    //     yieldOptimizer = _yieldOptimizer;
    //     executionFee = 50; // 0.5%
    // }

    function initialize(address _yieldOptimizer) public initializer {
        __Ownable_init(msg.sender);
        __ReentrancyGuard_init();
        yieldOptimizer = _yieldOptimizer;
        executionFee = 50; // 0.5%
    }

    /**
     * @dev Execute a yield farming strategy
     */
    function executeStrategy(
        ExecutionParams calldata params
    ) external onlyYieldOptimizer validProtocol(params.protocol) nonReentrant 
      returns (uint256 actualReturn) {
        
        require(params.amount > 0, "Invalid amount");
        require(params.deadline >= block.timestamp, "Execution deadline passed");
        
        bytes32 strategyId = keccak256(abi.encodePacked(
            params.protocol,
            params.token,
            params.amount,
            block.timestamp
        ));
        
        require(!executedStrategies[strategyId], "Strategy already executed");

        // Transfer tokens from YieldOptimizer
        IERC20(params.token).safeTransferFrom(yieldOptimizer, address(this), params.amount);

        // Execute strategy through protocol adapter
        try this._executeProtocolInteraction(params) returns (uint256 result) {
            actualReturn = result;
            require(actualReturn >= params.minReturn, "Insufficient return");
            
            executedStrategies[strategyId] = true;
            protocolAdapters[params.protocol].totalAllocated += params.amount;
            
            emit StrategyExecuted(strategyId, params.protocol, params.amount, actualReturn);
        } catch Error(string memory reason) {
            // Revert tokens back to YieldOptimizer on failure
            IERC20(params.token).safeTransfer(yieldOptimizer, params.amount);
            emit StrategyReverted(strategyId, reason);
            revert(reason);
        }
    }

    /**
     * @dev Internal function to execute protocol-specific interactions
     */
    function _executeProtocolInteraction(
        ExecutionParams calldata params
    ) external returns (uint256) {
        require(msg.sender == address(this), "Internal function");
        
        ProtocolAdapter storage adapter = protocolAdapters[params.protocol];
        
        // Approve tokens for protocol interaction using SafeERC20
        IERC20(params.token).safeIncreaseAllowance(adapter.adapterAddress, params.amount);
        
        // Call protocol adapter
        (bool success, bytes memory data) = adapter.adapterAddress.call(params.data);
        require(success, "Protocol interaction failed");
        
        return abi.decode(data, (uint256));
    }

    /**
     * @dev Withdraw from a specific protocol
     */
    function withdrawFromProtocol(
        address protocol,
        address token,
        uint256 amount
    ) external onlyYieldOptimizer validProtocol(protocol) nonReentrant 
      returns (uint256 actualAmount) {
        
        ProtocolAdapter storage adapter = protocolAdapters[protocol];
        
        // Call withdrawal through adapter
        bytes memory withdrawData = abi.encodeWithSignature(
            "withdraw(address,uint256)",
            token,
            amount
        );
        
        (bool success, bytes memory data) = adapter.adapterAddress.call(withdrawData);
        require(success, "Withdrawal failed");
        
        actualAmount = abi.decode(data, (uint256));
        adapter.totalAllocated -= amount;
        
        // Transfer withdrawn tokens back to YieldOptimizer
        IERC20(token).safeTransfer(yieldOptimizer, actualAmount);
    }

    /**
     * @dev Add protocol adapter
     */
    function addProtocolAdapter(
        address protocol,
        address adapter,
        string calldata protocolName
    ) external onlyOwner {
        require(protocol != address(0) && adapter != address(0), "Invalid addresses");
        
        protocolAdapters[protocol] = ProtocolAdapter({
            adapterAddress: adapter,
            protocolName: protocolName,
            isActive: true,
            totalAllocated: 0,
            averageAPY: 0
        });
        
        supportedProtocols.push(protocol);
        emit ProtocolAdapterAdded(protocol, adapter);
    }

    /**
     * @dev Update protocol status
     */
    function setProtocolStatus(address protocol, bool status) external onlyOwner {
        protocolAdapters[protocol].isActive = status;
    }

    /**
     * @dev Update protocol APY
     */
    function updateProtocolAPY(address protocol, uint256 newAPY) external onlyOwner {
        protocolAdapters[protocol].averageAPY = newAPY;
    }

    /**
     * @dev Emergency withdrawal function
     */
    function emergencyWithdraw(
        address protocol,
        address token
    ) external onlyOwner validProtocol(protocol) {
        ProtocolAdapter storage adapter = protocolAdapters[protocol];
        
        // Get balance from protocol
        bytes memory balanceData = abi.encodeWithSignature("getBalance(address)", token);
        (bool success, bytes memory data) = adapter.adapterAddress.call(balanceData);
        require(success, "Failed to get balance");
        
        uint256 balance = abi.decode(data, (uint256));
        
        if (balance > 0) {
            // Withdraw all funds
            bytes memory withdrawData = abi.encodeWithSignature(
                "emergencyWithdraw(address)",
                token
            );
            
            (success,) = adapter.adapterAddress.call(withdrawData);
            require(success, "Emergency withdrawal failed");
            
            // Transfer to owner
            IERC20(token).safeTransfer(owner(), balance);
            
            emit EmergencyWithdraw(protocol, token, balance);
        }
    }

    /**
     * @dev Get protocol information
     */
    function getProtocolInfo(address protocol) external view returns (
        address adapterAddress,
        string memory protocolName,
        bool isActive,
        uint256 totalAllocated,
        uint256 averageAPY
    ) {
        ProtocolAdapter storage adapter = protocolAdapters[protocol];
        return (
            adapter.adapterAddress,
            adapter.protocolName,
            adapter.isActive,
            adapter.totalAllocated,
            adapter.averageAPY
        );
    }

    /**
     * @dev Get all supported protocols
     */
    function getSupportedProtocols() external view returns (address[] memory) {
        return supportedProtocols;
    }

    /**
     * @dev Set execution fee
     */
    function setExecutionFee(uint256 _executionFee) external onlyOwner {
        require(_executionFee <= 1000, "Fee too high"); // Max 10%
        executionFee = _executionFee;
    }

    /**
     * @dev Calculate execution cost
     */
    function calculateExecutionCost(uint256 amount) external view returns (uint256) {
        return (amount * executionFee) / 10000;
    }
}

contracts/YieldOptimizer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title YieldOptimizer
 * @dev Core contract for automated yield farming optimization
 */
contract YieldOptimizer is 
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable
{
    using SafeERC20 for IERC20;

    // Structs
    struct Strategy {
        address protocol;
        address token;
        uint256 allocatedAmount;
        uint256 currentYield;
        uint256 riskScore;
        uint256 lastRebalance;
        bool isActive;
    }

    struct ProtocolInfo {
        address protocolAddress;
        string name;
        uint256 tvl;
        uint256 apy;
        uint256 riskScore;
        bool isWhitelisted;
    }

    struct UserPosition {
        uint256 totalDeposited;
        uint256 currentValue;
        uint256 pendingRewards;
        uint256 lastUpdate;
        mapping(bytes32 => uint256) strategyAllocations;
    }

    // State variables
    mapping(bytes32 => Strategy) public strategies;
    mapping(address => ProtocolInfo) public whitelistedProtocols;
    mapping(address => UserPosition) public userPositions;
    mapping(address => bool) public authorizedAI;
    
    bytes32[] public activeStrategies;
    address[] public supportedTokens;
    
    uint256 public constant MAX_PROTOCOL_ALLOCATION = 3000; // 30%
    uint256 public constant MIN_YIELD_THRESHOLD = 100; // 1%
    uint256 public constant REBALANCE_THRESHOLD = 500; // 5%
    uint256 public totalAssets;
    uint256 public performanceFee; // in basis points
    uint256 public managementFee; // in basis points
    
    // Events
    event StrategyCreated(bytes32 indexed strategyId, address protocol, address token);
    event StrategyExecuted(bytes32 indexed strategyId, uint256 amount, uint256 expectedYield);
    event StrategyRebalanced(bytes32 indexed strategyId, uint256 newAllocation);
    event EmergencyExit(bytes32 indexed strategyId, uint256 amount, string reason);
    event ProtocolWhitelisted(address protocol, string name);
    event AIAuthorized(address aiAgent, bool status);
    event UserDeposit(address indexed user, address token, uint256 amount);
    event UserWithdraw(address indexed user, address token, uint256 amount);

    // Modifiers
    modifier onlyAuthorizedAI() {
        require(authorizedAI[msg.sender] || msg.sender == owner(), "Not authorized AI");
        _;
    }

    modifier validStrategy(bytes32 strategyId) {
        require(strategies[strategyId].isActive, "Strategy not active");
        _;
    }

    // Initializer
    function initialize(
        address _owner,
        uint256 _performanceFee,
        uint256 _managementFee
    ) public initializer {
        __Ownable_init(_owner);
        __ReentrancyGuard_init();
        __Pausable_init();
        
        performanceFee = _performanceFee;
        managementFee = _managementFee;
    }

    /**
     * @dev Create a new yield farming strategy
     */
    function createStrategy(
        address protocol,
        address token,
        uint256 riskScore
    ) external onlyAuthorizedAI returns (bytes32) {
        require(whitelistedProtocols[protocol].isWhitelisted, "Protocol not whitelisted");
        require(riskScore <= 10000, "Invalid risk score");

        bytes32 strategyId = keccak256(abi.encodePacked(protocol, token, block.timestamp));
        
        strategies[strategyId] = Strategy({
            protocol: protocol,
            token: token,
            allocatedAmount: 0,
            currentYield: 0,
            riskScore: riskScore,
            lastRebalance: block.timestamp,
            isActive: true
        });

        activeStrategies.push(strategyId);
        emit StrategyCreated(strategyId, protocol, token);
        
        return strategyId;
    }

    /**
     * @dev Execute a yield farming strategy
     */
    function executeStrategy(
        bytes32 strategyId,
        uint256 amount
    ) external onlyAuthorizedAI validStrategy(strategyId) nonReentrant {
        Strategy storage strategy = strategies[strategyId];
        require(amount > 0, "Amount must be greater than 0");
        
        // Check allocation limits
        uint256 protocolAllocation = getProtocolAllocation(strategy.protocol) + amount;
        require(
            protocolAllocation <= (totalAssets * MAX_PROTOCOL_ALLOCATION) / 10000,
            "Exceeds protocol allocation limit"
        );

        // Update strategy
        strategy.allocatedAmount += amount;
        totalAssets += amount;

        emit StrategyExecuted(strategyId, amount, strategy.currentYield);
    }

    /**
     * @dev Rebalance strategy allocation
     */
    function rebalanceStrategy(
        bytes32 strategyId,
        uint256 newAllocation
    ) external onlyAuthorizedAI validStrategy(strategyId) {
        Strategy storage strategy = strategies[strategyId];
        
        uint256 difference = newAllocation > strategy.allocatedAmount 
            ? newAllocation - strategy.allocatedAmount
            : strategy.allocatedAmount - newAllocation;
            
        require(
            (difference * 10000) / strategy.allocatedAmount >= REBALANCE_THRESHOLD,
            "Rebalance threshold not met"
        );

        strategy.allocatedAmount = newAllocation;
        strategy.lastRebalance = block.timestamp;

        emit StrategyRebalanced(strategyId, newAllocation);
    }

    /**
     * @dev Emergency exit from a strategy
     */
    function emergencyExit(
        bytes32 strategyId,
        string calldata reason
    ) external onlyAuthorizedAI validStrategy(strategyId) {
        Strategy storage strategy = strategies[strategyId];
        uint256 amount = strategy.allocatedAmount;
        
        strategy.allocatedAmount = 0;
        strategy.isActive = false;
        totalAssets -= amount;

        emit EmergencyExit(strategyId, amount, reason);
    }

    /**
     * @dev User deposit function
     */
    function deposit(address token, uint256 amount) external nonReentrant whenNotPaused {
        require(amount > 0, "Amount must be greater than 0");
        require(isTokenSupported(token), "Token not supported");

        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        
        UserPosition storage position = userPositions[msg.sender];
        position.totalDeposited += amount;
        position.lastUpdate = block.timestamp;
        
        // Update total assets
        totalAssets += amount;

        emit UserDeposit(msg.sender, token, amount);
    }

    /**
     * @dev User withdrawal function
     */
    function withdraw(address token, uint256 amount) external nonReentrant {
        UserPosition storage position = userPositions[msg.sender];
        require(position.totalDeposited >= amount, "Insufficient balance");

        position.totalDeposited -= amount;
        position.lastUpdate = block.timestamp;
        
        // Update total assets
        totalAssets -= amount;

        IERC20(token).safeTransfer(msg.sender, amount);

        emit UserWithdraw(msg.sender, token, amount);
    }

    /**
     * @dev Whitelist a DeFi protocol
     */
    function whitelistProtocol(
        address protocol,
        string calldata name,
        uint256 riskScore
    ) external onlyOwner {
        whitelistedProtocols[protocol] = ProtocolInfo({
            protocolAddress: protocol,
            name: name,
            tvl: 0,
            apy: 0,
            riskScore: riskScore,
            isWhitelisted: true
        });

        emit ProtocolWhitelisted(protocol, name);
    }

    /**
     * @dev Authorize AI agent
     */
    function authorizeAI(address aiAgent, bool status) external onlyOwner {
        authorizedAI[aiAgent] = status;
        emit AIAuthorized(aiAgent, status);
    }

    /**
     * @dev Add supported token
     */
    function addSupportedToken(address token) external onlyOwner {
        require(token != address(0), "Invalid token address");
        require(!isTokenSupported(token), "Token already supported");
        supportedTokens.push(token);
    }

    /**
     * @dev Remove supported token
     */
    function removeSupportedToken(address token) external onlyOwner {
        require(isTokenSupported(token), "Token not supported");
        for (uint i = 0; i < supportedTokens.length; i++) {
            if (supportedTokens[i] == token) {
                supportedTokens[i] = supportedTokens[supportedTokens.length - 1];
                supportedTokens.pop();
                break;
            }
        }
    }

    /**
     * @dev Update strategy yield
     */
    function updateStrategyYield(
        bytes32 strategyId,
        uint256 newYield
    ) external onlyAuthorizedAI validStrategy(strategyId) {
        strategies[strategyId].currentYield = newYield;
    }

    /**
     * @dev Get protocol allocation
     */
    function getProtocolAllocation(address protocol) public view returns (uint256) {
        uint256 totalAllocation = 0;
        for (uint i = 0; i < activeStrategies.length; i++) {
            if (strategies[activeStrategies[i]].protocol == protocol) {
                totalAllocation += strategies[activeStrategies[i]].allocatedAmount;
            }
        }
        return totalAllocation;
    }

    /**
     * @dev Check if token is supported
     */
    function isTokenSupported(address token) public view returns (bool) {
        for (uint i = 0; i < supportedTokens.length; i++) {
            if (supportedTokens[i] == token) {
                return true;
            }
        }
        return false;
    }

    /**
     * @dev Get user position
     */
    function getUserPosition(address user) external view returns (
        uint256 totalDeposited,
        uint256 currentValue,
        uint256 pendingRewards,
        uint256 lastUpdate
    ) {
        UserPosition storage position = userPositions[user];
        return (
            position.totalDeposited,
            position.currentValue,
            position.pendingRewards,
            position.lastUpdate
        );
    }

    /**
     * @dev Get strategy details
     */
    function getStrategy(bytes32 strategyId) external view returns (Strategy memory) {
        return strategies[strategyId];
    }

    /**
     * @dev Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

